import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './style.css';
import Navbar from './components/Navbar';
import Popup from './components/Popup';
import LoginForm from './components/LoginForm';
import SignupForm from './components/SignupForm';
import Home from './components/Home';
import SportsCategories from './components/SportsCategories';
import CategoryPage from './components/CategoryPage';

function App() {
  const [showPopup, setShowPopup] = useState(false);
  const [isSignup, setIsSignup] = useState(false);
  const [error, setError] = useState('');

  const [loginData, setLoginData] = useState({
    email: '',
    password: ''
  });

  const [signupData, setSignupData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData)
      });
      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('user', JSON.stringify(data));
        setShowPopup(false);
        alert('Login successful! Redirecting to homepage...');
      } else {
        setError('Login failed. Please check your credentials.');
      }
    } catch (err) {
      setError('Server error. Please try again later.');
    }
  };

  const handleSignupSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await fetch('http://localhost:8080/api/auth/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(signupData)
      });
      if (response.ok) {
        setShowPopup(false);
        setIsSignup(false);
        alert('Signup successful! Please log in.');
      } else {
        setError('Signup failed. Email or username may already exist.');
      }
    } catch (err) {
      setError('Server error. Please try again later.');
    }
  };

  return (
    <Router>
      <div>
        <Navbar onLoginClick={() => setShowPopup(true)} />

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/sports" element={<SportsCategories />} />
          <Route path="/sports/:categoryId" element={<CategoryPage />} />
          <Route path="/community" element={<div>Community Page Coming Soon</div>} />
        </Routes>

        {showPopup && (
          <Popup showPopup={showPopup} isSignup={isSignup} onClose={() => setShowPopup(false)}>
            {!isSignup ? (
              <LoginForm
                loginData={loginData}
                setLoginData={setLoginData}
                handleLoginSubmit={handleLoginSubmit}
                error={error}
                onSignupClick={() => setIsSignup(true)}
              />
            ) : (
              <SignupForm
                signupData={signupData}
                setSignupData={setSignupData}
                handleSignupSubmit={handleSignupSubmit}
                error={error}
                onLoginClick={() => setIsSignup(false)}
              />
            )}
          </Popup>
        )}
      </div>
    </Router>
  );
}

export default App;
