import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import logo from '../images/logo.jpg'; // Adjust path as needed

const Navbar = ({ onLoginClick, user, onLogout }) => {
  const navigate = useNavigate();

  const handleNavClick = (e, path) => {
    if (!user) {
      e.preventDefault(); // Prevent navigation if not logged in
      onLoginClick(); // Show login popup
    } else {
      navigate(path); // Navigate to the path if logged in
    }
  };

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  return (
    <header>
      <nav className="navbar">
        <span className="hamburger-btn material-symbols-rounded">menu</span>
        <Link to="/" className="logo">
          <img src={logo} alt="FanNation Logo" />
          <h2>FanNation</h2>
        </Link>
        <ul className="links">
          <span className="close-btn material-symbols-rounded">close</span>
          <li>
            <Link to="/" onClick={(e) => handleNavClick(e, '/')}>Home</Link>
          </li>
          <li>
            <Link to="/sports" onClick={(e) => handleNavClick(e, '/sports')}>Sports</Link>
          </li>
          <li>
            <Link to="/community" onClick={(e) => handleNavClick(e, '/community')}>Community</Link>
          </li>
        </ul>
        {user ? (
          <button className="login-btn" onClick={handleLogout}>
            LOG OUT
          </button>
        ) : (
          <button className="login-btn" onClick={onLoginClick}>
            LOG IN
          </button>
        )}
      </nav>
    </header>
  );
};

export default Navbar;
