// frontend/src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import './style.css';
import logo from './images/logo.jpg';
import Home from './components/Home';
import SportsCategories from './components/SportsCategories';
import CategoryPage from './components/CategoryPage';

function App() {
  const [showPopup, setShowPopup] = useState(false);
  const [isSignup, setIsSignup] = useState(false);
  const [error, setError] = useState('');

  const [loginData, setLoginData] = useState({
    email: '',
    password: ''
  });

  const [signupData, setSignupData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData)
      });
      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('user', JSON.stringify(data));
        setShowPopup(false);
        alert('Login successful! Redirecting to homepage...');
      } else {
        setError('Login failed. Please check your credentials.');
      }
    } catch (err) {
      setError('Server error. Please try again later.');
    }
  };

  const handleSignupSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await fetch('http://localhost:8080/api/auth/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(signupData)
      });
      if (response.ok) {
        setShowPopup(false);
        setIsSignup(false);
        alert('Signup successful! Please log in.');
      } else {
        setError('Signup failed. Email or username may already exist.');
      }
    } catch (err) {
      setError('Server error. Please try again later.');
    }
  };

  return (
    <Router>
    <div>
      <header>
        <nav className="navbar">
          <span className="hamburger-btn material-symbols-rounded">menu</span>
          <Link to="/" className="logo">
            <img src={logo} alt="FanNation Logo" />
            <h2>FanNation</h2>
          </Link>
          <ul className="links">
            <span className="close-btn material-symbols-rounded">close</span>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/sports">Sports</Link></li>
            <li><Link to="/community">Community</Link></li>
          </ul>
          <button className="login-btn" onClick={() => setShowPopup(true)}>LOG IN</button>
        </nav>
      </header>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/sports" element={<SportsCategories />} />
        <Route path="/sports/:categoryId" element={<CategoryPage />} />
        <Route path="/community" element={<div>Community Page Coming Soon</div>} />
      </Routes>

        {showPopup && (
          <>
            <div className="blur-bg-overlay" onClick={() => setShowPopup(false)}></div>
            <div className={`form-popup ${showPopup ? 'show-popup' : ''} ${isSignup ? 'show-signup' : ''}`}>
              <span className="close-btn material-symbols-rounded" onClick={() => setShowPopup(false)}>close</span>
              {!isSignup && (
                <div className="form-box login">
                  <div className="form-details">
                    <h2>Welcome Back</h2>
                    <p>Please log in with your credentials to connect with fellow sports fans.</p>
                  </div>
                  <div className="form-content">
                    <h2>LOGIN</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <form onSubmit={handleLoginSubmit}>
                      <div className="input-field">
                        <input 
                          type="text" 
                          name="email" 
                          value={loginData.email}
                          onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                          required 
                        />
                        <label>Email</label>
                      </div>
                      <div className="input-field">
                        <input 
                          type="password" 
                          name="password" 
                          value={loginData.password}
                          onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                          required 
                        />
                        <label>Password</label>
                      </div>
                      <a href="/#" className="forgot-pass-link">Forgot password?</a>
                      <button type="submit">Log In</button>
                    </form>
                    <div className="bottom-link">
                      Don't have an account?{' '}
                      <button 
                        type="button" 
                        className="link-button" 
                        onClick={() => setIsSignup(true)}
                      >
                        Signup
                      </button>
                    </div>
                  </div>
                </div>
              )}
              {isSignup && (
                <div className="form-box signup">
                  <div className="form-details">
                    <h2>Create Account</h2>
                    <p>Join FanNation and connect with fans of all sports.</p>
                  </div>
                  <div className="form-content">
                    <h2>SIGNUP</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <form onSubmit={handleSignupSubmit}>
                      <div className="input-field">
                        <input 
                          type="text" 
                          name="username" 
                          value={signupData.username}
                          onChange={(e) => setSignupData({ ...signupData, username: e.target.value })}
                          required 
                        />
                        <label>Username</label>
                      </div>
                      <div className="input-field">
                        <input 
                          type="text" 
                          name="email" 
                          value={signupData.email}
                          onChange={(e) => setSignupData({ ...signupData, email: e.target.value })}
                          required 
                        />
                        <label>Email</label>
                      </div>
                      <div className="input-field">
                        <input 
                          type="password" 
                          name="password" 
                          value={signupData.password}
                          onChange={(e) => setSignupData({ ...signupData, password: e.target.value })}
                          required 
                        />
                        <label>Password</label>
                      </div>
                      <div className="policy-text">
                        <input type="checkbox" id="policy" required />
                        <label htmlFor="policy">
                          I agree to the{' '}
                          <a href="/#" className="option">Terms & Conditions</a>
                        </label>
                      </div>
                      <button type="submit">Sign Up</button>
                    </form>
                    <div className="bottom-link">
                      Already have an account?{' '}
                      <button 
                        type="button" 
                        className="link-button" 
                        onClick={() => setIsSignup(false)}
                      >
                        Login
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </Router>
  );
}

export default App;
