import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import 'D:\education\University\sem 6\SE\FanNation\FanNation\frontend\src\style.css'; // Use the same CSS as App.js

const SportsCategories = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch categories from backend
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/categories', {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });
        if (response.ok) {
          const data = await response.json();
          setCategories(data);
        } else {
          setError('Failed to load categories.');
        }
      } catch (err) {
        setError('Server error. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    fetchCategories();
  }, []);

  return (
    <div className="sports-categories-page">
      <main>
        <h1>Sports Categories</h1>
        {loading && <p>Loading categories...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {!loading && !error && (
          <div className="categories-container">
            {categories.length > 0 ? (
              categories.map((category) => (
                <div key={category.id} className="category-card">
                  <h2>{category.name}</h2>
                  <p>{category.description || 'No description available.'}</p>
                  <Link to={`/sports/${category.id}`} className="category-link">
                    Explore {category.name}
                  </Link>
                </div>
              ))
            ) : (
              <p>No categories available.</p>
            )}
          </div>
        )}
      </main>
    </div>
  );
};

export default SportsCategories;
